shaders.vert:
#define _IS_VERT_SHADER 1

precision highp float;

uniform mat4 cc_matViewProj;

#if _USE_MODEL
  uniform mat4 cc_matWorld;
#endif

attribute vec3 a_position;
attribute lowp vec4 a_color;

#if USE_TEXTURE
  attribute mediump vec2 a_uv0;
  varying mediump vec2 v_uv0;
#endif

varying lowp vec4 v_color;

void main () {
  mat4 mvp;

  #if _USE_MODEL
    mvp = cc_matViewProj * cc_matWorld;
  #else
    mvp = cc_matViewProj;
  #endif

  #if USE_TEXTURE
    v_uv0 = a_uv0;
  #endif

  v_color = a_color;

  gl_Position = mvp * vec4(a_position, 1);
}

shaders.frag:
#define _IS_FRAG_SHADER 1

precision highp float;

#if USE_TEXTURE
  uniform sampler2D texture;
  varying mediump vec2 v_uv0;
#endif

#if USE_ALPHA_TEST
  uniform float alphaThreshold;
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

varying lowp vec4 v_color;

uniform float bluramount;
uniform int isblurs;

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
  vec4 sum = vec4(0.0);
  vec2 v1 = vec2(bluramount, bluramount);
  vec2 v2 = vec2(-bluramount, bluramount);

  sum += texture2D(texture, v_uv0 - 0.4 * v1) * 0.01;
  sum += texture2D(texture, v_uv0 - 0.35 * v2) * 0.03;
	sum += texture2D(texture, v_uv0 - 0.3 * v1) * 0.05;
  sum += texture2D(texture, v_uv0 - 0.25 * v2) * 0.07;
	sum += texture2D(texture, v_uv0 - 0.2 * v1) * 0.09;
  sum += texture2D(texture, v_uv0 - 0.15 * v2) * 0.11;
	sum += texture2D(texture, v_uv0 - 0.1 * v1) * 0.13;
  sum += texture2D(texture, v_uv0             ) * 0.15;
	sum += texture2D(texture, v_uv0 + 0.1 * v1) * 0.13;
  sum += texture2D(texture, v_uv0 + 0.15 * v2) * 0.11;
  sum += texture2D(texture, v_uv0 + 0.2 * v1) * 0.09;
  sum += texture2D(texture, v_uv0 + 0.25 * v2) * 0.07;
  sum += texture2D(texture, v_uv0 + 0.3 * v1) * 0.05;
  sum += texture2D(texture, v_uv0 + 0.35 * v2) * 0.03;
  sum += texture2D(texture, v_uv0 + 0.4 * v1) * 0.01;

  vec4 vectemp = vec4(0,0,0,0);
  vec4 substract = vec4(0,0,0,0);
  vectemp = (sum - substract) * v_color;


  float alpha = texture2D(texture, v_uv0).a;
  if (alpha < 0.05) { 
    fragColor = vec4(0.);
  } else {
    fragColor = vectemp;
  }
}

void main () {
  vec4 color = v_color;

  #if USE_TEXTURE
    color *= texture2D(texture, v_uv0);
    if (isblurs == 1) {
      mainImage(color, gl_FragCoord.xy);
    }
    #if _USE_ETC1_TEXTURE
      color.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
    #endif
  #endif

  ALPHA_TEST(color);

  gl_FragColor = color;
}
